    "name": "CsvToPatient",
    "status": "active",
    "structure": [
        {
            "url": "http://hl7.org/fhir/StructureDefinition/Patient",
            "mode": "target"
        }
    ],
    "group": [
        {
            "name": "CsvToPatient",
            "typeMode": "none",
            "input": [
                {
                    "name": "source",
                    "type": "CSVRecord",
                    "mode": "source"
                },
                {
                    "name": "target",
                    "type": "Patient",
                    "mode": "target"
                }
            ],
            "rule": [
                {
                    "name": "Id",
                    "source": [
                        {
                            "context": "source",
                            "element": "id"
                        }
                    ],
                    "target": [
                        {
                            "context": "target",
                            "contextType": "variable",
                            "element": "id",
                            "transform": "copy"
                        }
                    ]
                },
                {
                    "name": "Name",
                    "source": [
                        {
                            "context": "source",
                            "element": "lastName"
                        }
                    ],
                    "target": [
                        {
                            "context": "target",
                            "contextType": "variable",
                            "element": "name.family",
                            "transform": "copy"
                        }
                    ]
                },
                {
                    "name": "FirstName",
                    "source": [
                        {
                            "context": "source",
                            "element": "firstName"
                        }
                    ],
                    "target": [
                        {
                            "context": "target",
                            "contextType": "variable",
                            "element": "name.given",
                            "transform": "copy"
                        }
                    ]
                },
                {
                    "name": "Gender",
                    "source": [
                        {
                            "context": "source",
                            "element": "gender"
                        }
                    ],
                    "target": [
                        {
                            "context": "target",
                            "contextType": "variable",
                            "element": "gender",
                            "transform": "translate",
                            "parameter": [
                                {
                                    "valueString": "http://hl7.org/fhir/ValueSet/administrative-gender"
                                }
                            ]
                        }
                    ]
                },
                {
                    "name": "BirthDate",
                    "source": [
                        {
                            "context": "source",
                            "element": "birthDate"
                        }
                    ],
                    "target": [
                        {
                            "context": "target",
